import Task from "./Task";
import { DragDropContext, Droppable, Draggable } from "react-beautiful-dnd";
import './TaskList.css';
import { useState, useEffect } from "react";

const TaskList = ({ data, id }) => {
    const [tasks, setTasks] = useState(data);
    
    return (
        <div className="task-list" key={ id }>
            <h2 id="title">{ data.name }</h2>
            <Droppable droppableId={ id } key={ id }>
                {(provided, snapshot) => {
                    return(
                        <div className='drop-area' {...provided.droppableProps} ref={provided.innerRef} style={{ background: snapshot.isDraggingOver ? "lightblue": "white" }}>
                            {data.items.map((item, index) => {
                                return (
                                    <div key={ item.id }>
                                        <Task item={item} index={index}/>
                                    </div>
                                )
                            })}
                            {provided.placeholder}
                        </div>
                    );
                }}
            </Droppable>
        </div>
    );
}
 
export default TaskList;
