import { useState } from 'react';
import './Board.css';
import TaskList from './TaskList';
import uuid from "uuid/v4";
import { DragDropContext } from "react-beautiful-dnd";

const onDragEnd = (result, columns, setColumns) => {
  if (!result.destination) return;
  const { source, destination } = result;

  if (source.droppableId !== destination.droppableId) {
    const sourceColumn = columns[source.droppableId];
    const destColumn = columns[destination.droppableId];
    const sourceItems = [...sourceColumn.items];
    const destItems = [...destColumn.items];
    const [removed] = sourceItems.splice(source.index, 1);
    destItems.splice(destination.index, 0, removed);
    setColumns({
      ...columns,
      [source.droppableId]: {
        ...sourceColumn,
        items: sourceItems
      },
      [destination.droppableId]: {
        ...destColumn,
        items: destItems
      }
    });
  } else {
    const column = columns[source.droppableId];
    const copiedItems = [...column.items];
    const [removed] = copiedItems.splice(source.index, 1);
    copiedItems.splice(destination.index, 0, removed);
    setColumns({
      ...columns,
      [source.droppableId]: {
        ...column,
        items: copiedItems
      }
    });
  }
};

const Board = ({ newTask, lists }) => {
    const [columns, setColumns] = useState(lists);
    console.log(lists[1])
    // let previousTask = lists.items.some( item => item['id'] === newTask.id)
    // if (!previousTask && newTask.id !== 0){
    //   itemsFromBackend.push(newTask)
    // }

    return (
        <div className="board">
          <DragDropContext onDragEnd={result => onDragEnd(result, columns, setColumns)}>
            {Object.entries(columns).map(([columnId, column], index) => {
              return (
                <div key={columnId}>
                  <TaskList data={ column } id={ columnId }/>
                </div>
              )
            })}
          </DragDropContext>
        </div>
    );
}
 
export default Board;
