import { useState } from 'react';
import './Task.css'

const Task = ({title, author, body}) => {
    const [showDetails, setShowDetails] = useState(false)
    const onClick = () => setShowDetails(true)
    
    return (
        <div className="task">
            <div className="collapse">
                <h2>{ title }</h2>
                <h3>{ author }</h3>
            </div>
        </div>
    );
}
 
export default Task;
