import { useState } from 'react';
import './Task.css'

const Task = ({title, author, body}) => {
    const [showDetails, setShowDetails] = useState(false)
    const onClick = () => setShowDetails(!showDetails)
    
    return (
        <div className="task" onClick={onClick}>
            <div className="collapse">
                {showDetails ? (
                    <h2>hello</h2>
                ): null}
            </div>
        </div>
    );
}
 
export default Task;
