import Navbar from './Navbar';
import { BrowserRouter as Router, Route, Switch } from 'react-router-dom'
import Board from './Board';
import Modal from './Modal';
import { useContext, useEffect, useState, useRef } from 'react';

function App() {
  const openModal = useRef(false);
  const modalData = useRef(null);

  const open = () => {
    openModal.current = true;
    console.log(openModal);
  }
  return (
    <div className="App">
      <Router>
        <Navbar/>
        <div className="content">
          <button id="addTask" onClick={() => openModal.current = true}>Open Modal</button>
          <Modal open={ openModal } onClose={() => openModal.current = false } onSubmit={(data) => {
            modalData.current = data;
            openModal.current = false;
            console.log(modalData);
            console.log(openModal)
          }}/>
          <Board newTask={modalData}/>
        </div>
      </Router>
    </div>
  );
}

export default App;
