import { useState } from 'react';
import './Task.css'

const Task = ({title, author, body}) => {
    const [showDetails, setShowDetails] = useState(false)
    const onClick = () => {
        setShowDetails(true)
        console.log("hello")
    }
    
    return (
        <div className="task">
            <input type="submit" value="Search" onClick={onClick} />
            { showDetails ? <h2>hello</h2> : null }
        </div>
    );
}
 
export default Task;
